mapper.select:
select 

from life_player_achievement
where player_id = #{playerId,jdbcType=BIGINT} and achievement_id = #{achievementId,jdbcType=BIGINT}

mapper.update:
update life_player_achievement
set player_id = #{playerId,jdbcType=BIGINT},
  achievement_id = #{achievementId,jdbcType=BIGINT},
  completed_time = #{completedTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}
