mapper.select:
select 

from life_team_member
where team_id = #{teamId,jdbcType=BIGINT} and player_id = #{playerId,jdbcType=BIGINT}

mapper.update:
update life_team_member
set team_id = #{teamId,jdbcType=BIGINT},
  player_id = #{playerId,jdbcType=BIGINT},
  member_status = #{memberStatus,jdbcType=INTEGER},
  join_time = #{joinTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}
